#[cfg(test)]
mod tests {
    use bls_signatures::Serialize;
    use cid::Cid;
    use fil_actor_eam::Return;
    use fil_actor_evm::{Method as EvmMethods};
    use fil_actors_runtime::EAM_ACTOR_ADDR;
    use fvm::executor::{ApplyKind, Executor};
    use fvm::state_tree::ActorState;
    use fvm_integration_tests::bundle;
    use fvm_integration_tests::dummy::DummyExterns;
    use fvm_integration_tests::tester::{Account, Tester};
    use fvm_ipld_blockstore::MemoryBlockstore;
    use fvm_ipld_encoding::strict_bytes;
    use fvm_ipld_encoding::tuple::*;
    use fvm_ipld_encoding::RawBytes;
    use fvm_ipld_encoding::serde_bytes;
    use fvm_shared::address::Address;
    use fvm_shared::econ::TokenAmount;
    use fvm_shared::message::Message;
    use fvm_shared::state::StateTreeVersion;
    use fvm_shared::version::NetworkVersion;
    use fvm_ipld_encoding::CborStore;
    use multihash::Code;
    use std::env;
    use fvm::machine::Manifest;
    use serde::{Deserialize as SerdeDeserialize, Serialize as SerdeSerialize};

    const WASM_COMPILED_PATH: &str = "../build/v0.8/tests/AccountApiTest.bin";

    #[derive(SerdeSerialize, SerdeDeserialize)]
    #[serde(transparent)]
    pub struct CreateExternalParams(#[serde(with = "strict_bytes")] pub Vec<u8>);

    #[derive(Debug, Serialize_tuple, Deserialize_tuple)]
    pub struct AuthenticateMessageParams {
        #[serde(with = "serde_bytes")]
        pub signature: Vec<u8>,
        #[serde(with = "serde_bytes")]
        pub message: Vec<u8>,
    }

    #[derive(Serialize_tuple, Deserialize_tuple, PartialEq, Eq, Clone, Debug)]
    pub struct UniversalReceiverParams {
        /// Asset type
        pub type_: u32,
        /// Payload corresponding to asset type
        pub payload: RawBytes,
    }

    #[test]
    fn account_tests() {
        println!("Testing solidity API");

        let bs = MemoryBlockstore::default();
        let actors = std::fs::read("./builtin-actors/output/builtin-actors-hyperspace.car")
            .expect("Unable to read actor devnet file");
        let bundle_root = bundle::import_bundle(&bs, &actors).unwrap();

        let (manifest_version, manifest_data_cid): (u32, Cid) = bs.get_cbor(&bundle_root).unwrap().unwrap();
        let manifest = Manifest::load(&bs, &manifest_data_cid, manifest_version).unwrap();

        let mut tester =
            Tester::new(NetworkVersion::V18, StateTreeVersion::V5, bundle_root, bs).unwrap();

        let sender: [Account; 1] = tester.create_accounts().unwrap();

        // Instantiate Account Actor with a BLS address (not secure private key! Only use for tests!)
        let bls_private_key_provider = bls_signatures::PrivateKey::new(hex::decode("deadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef").unwrap());
        let worker = Address::new_bls(&bls_private_key_provider.public_key().as_bytes()).unwrap();

        let state_tree = tester.state_tree.as_mut().unwrap();
        let assigned_addr = state_tree.register_new_address(&worker).unwrap();
        let state = fvm::account_actor::State { address: worker };

        let cid = state_tree
            .store()
            .put_cbor(&state, Code::Blake2b256)
            .unwrap();

        let actor_state = ActorState {
            code: *manifest.get_account_code(),
            state: cid,
            sequence: 0,
            balance: TokenAmount::from_whole(1_000_000),
            delegated_address: Some(worker),
        };

        state_tree.set_actor(assigned_addr, actor_state).unwrap();

        // Instantiate machine
        tester.instantiate_machine(DummyExterns).unwrap();

        let executor = tester.executor.as_mut().unwrap();

        println!("Calling init actor (EVM)");

        let wasm_path = env::current_dir()
            .unwrap()
            .join(WASM_COMPILED_PATH)
            .canonicalize()
            .unwrap();
        let evm_hex = std::fs::read(wasm_path).expect("Unable to read file");
        let evm_bin = hex::decode(evm_hex).unwrap();

        let constructor_params = CreateExternalParams(evm_bin);

        let message = Message {
            from: sender[0].1,
            to: EAM_ACTOR_ADDR,
            gas_limit: 1000000000,
            method_num: 4,
            params: RawBytes::serialize(constructor_params).unwrap(),
            ..Message::default()
        };

        let len = message.params.len();
        let res = executor
            .execute_message(message, ApplyKind::Explicit, len)
            .unwrap();
        
        assert_eq!(res.msg_receipt.exit_code.value(), 0);

        let exec_return: Return = RawBytes::deserialize(&res.msg_receipt.return_data).unwrap();
        let actor_id = exec_return.actor_id;

        println!("Calling `authenticate_message`");

        let sig = bls_private_key_provider.sign(hex::decode("").unwrap());
        dbg!(hex::encode(sig.as_bytes()));
        dbg!(hex::encode(worker.to_bytes()));

        // create message
        let message = Message {
            from: sender[0].1,
            to: Address::new_id(actor_id),
            gas_limit: 1000000000,
            method_num: EvmMethods::InvokeContract as u64,
            sequence: 1,
            // target: 0x0390a40613dfb06445dfc3759ec22146d66b832afe57b4ac441e5209d154131b1540e937cb837831855553e17eefeeeed1(assigned_addr), params: ["0x8eabea2a4001061ac4c9fe3c517725b8829b159149a863b2a2320cc628d026a871d3cb34947371f384a9eb49ff9bd56a019fa70e10c06ac5ca93df3c1d6f54d540c57cbe2f5209cafdc12146d5d59172dd4d8359015e10584fa6327de0ce5a6a", ""]
            params: RawBytes::new(hex::decode("").unwrap()),
            ..Message::default()
        };

        let len = message.params.len();
        let res = executor
            .execute_message(message, ApplyKind::Explicit, len)
            .unwrap();

        dbg!(&res);

        assert_eq!(res.msg_receipt.exit_code.value(), 0);

        println!("Calling `authenticate_message (actor ID)`");

        let sig = bls_private_key_provider.sign(hex::decode("").unwrap());
        dbg!(hex::encode(sig.as_bytes()));
        dbg!(hex::encode(worker.to_bytes()));

        // create message
        let message = Message {
            from: sender[0].1,
            to: Address::new_id(actor_id),
            gas_limit: 1000000000,
            method_num: EvmMethods::InvokeContract as u64,
            sequence: 2,
            // target: 101(assigned_addr actor id), params: ["0x8eabea2a4001061ac4c9fe3c517725b8829b159149a863b2a2320cc628d026a871d3cb34947371f384a9eb49ff9bd56a019fa70e10c06ac5ca93df3c1d6f54d540c57cbe2f5209cafdc12146d5d59172dd4d8359015e10584fa6327de0ce5a6a", ""]
            params: RawBytes::new(hex::decode("").unwrap()),
            ..Message::default()
        };

        let len = message.params.len();
        let res = executor
            .execute_message(message, ApplyKind::Explicit, len)
            .unwrap();

        assert_eq!(res.msg_receipt.exit_code.value(), 0);

        println!("Calling `universal_receiver_hook`");

        let message = Message {
            from: sender[0].1,
            to: Address::new_id(actor_id),
            gas_limit: 1000000000,
            method_num: EvmMethods::InvokeContract as u64,
            sequence: 3,
            // target: 0x0390a40613dfb06445dfc3759ec22146d66b832afe57b4ac441e5209d154131b1540e937cb837831855553e17eefeeeed1(assigned_addr), params: UniversalReceiverParams{0, RawBytes([0,1,2])}
            params: RawBytes::new(hex::decode("").unwrap()),
            ..Message::default()
        };

        let res = executor
            .execute_message(message, ApplyKind::Explicit, 100)
            .unwrap();

        assert_eq!(res.msg_receipt.exit_code.value(), 0);

        println!("Calling `universal_receiver_hook (actor ID)`");

        let message = Message {
            from: sender[0].1,
            to: Address::new_id(actor_id),
            gas_limit: 1000000000,
            method_num: EvmMethods::InvokeContract as u64,
            sequence: 4,
            // target: 101(assigned_addr actor id), params: UniversalReceiverParams{0, RawBytes([0,1,2])}
            params: RawBytes::new(hex::decode("").unwrap()),
            ..Message::default()
        };

        let res = executor
            .execute_message(message, ApplyKind::Explicit, 100)
            .unwrap();

        assert_eq!(res.msg_receipt.exit_code.value(), 0);
    }

}

